import React, { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Movies from './pages/Movies';
import About from './pages/About';
import AddMovieForm from './components/AddMovieForm';

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

  // Charger les films au d√©marrage
  useEffect(() => {
    fetch(`${API_URL}/movies`)
      .then(res => {
        if (!res.ok) throw new Error('Erreur lors du chargement des films');
        return res.json();
      })
      .then(data => {
        setMovies(data);
        setIsLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setIsLoading(false);
      });
  }, [API_URL]);

  // Ajouter un nouveau film
  function addMovie(newMovie) {
    setMovies([...movies, newMovie]);
  }

  // Supprimer un film
  function deleteMovie(id) {
    if (window.confirm("Do you want to delete this movie?")) {
      fetch(`${API_URL}/movies/${id}`, { method: 'DELETE' })
        .then(() => {
          setMovies(movies.filter(movie => movie.id !== id));
        })
        .catch(err => alert("Error  by delete"));
    }
  }

  return (
    <>
      <Navbar />
      <main style={{ padding: '20px' }}>
        {error && <p style={{ color: 'red' }}> {error}</p>}
        {isLoading ? (
          <p>üìΩÔ∏è Download new movie...</p>
        ) : (
          <Routes>
            <Route path="/" element={<Home />} />
            <Route
              path="/movies"
              element={
                <>
                  <AddMovieForm addMovie={addMovie} />
                  {movies.length === 0 ? (
                    <p>No movie now.</p>
                  ) : (
                    <ul>
                      {movies.map(movie => (
                        <li key={movie.id} style={{ marginBottom: '10px' }}>
                          <strong>{movie.title}</strong> ‚Äî {movie.director}
                          <button
                            onClick={() => deleteMovie(movie.id)}
                            style={{ marginLeft: '10px' }}
                          >
                            Delete
                          </button>
                        </li>
                      ))}
                    </ul>
                  )}
                </>
              }
            />
            <Route path="/about" element={<About />} />
          </Routes>
        )}
      </main>
    </>
  );
}

export default App;
