export default function About() {
  return (
    <div style={{ padding: '20px' }}>
      <h2>About</h2>
      <p>This movie app was created as part of a Phase 2 React project for a web development curriculum.</p>
      <p>It demonstrates key concepts such as component architecture, client-side routing, RESTful API integration using json-server, and form handling.</p>
      <p>Author: Marc-Arthur Edouard</p>
      <p>Project Date: July 2025</p>
    </div>
  );
}
