import React, { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Movies from './pages/Movies';
import Home from './pages/Home';
import About from './pages/About';

const TMDB_API_KEY = 'TA_CLE_API_TMDB';
const TMDB_BASE_URL = 'https://api.themoviedb.org/3';

function App() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch(`${TMDB_BASE_URL}/movie/popular?api_key=${TMDB_API_KEY}&language=en-US&page=1`)
      .then(res => res.json())
      .then(data => {
        if (data.results) {
        
          const moviesWithLikes = data.results.map(movie => ({ ...movie, likes: 0 }));
          setMovies(moviesWithLikes);
        }
      })
      .catch(console.error);
  }, []);

  function addLike(id) {
    setMovies(movies.map(movie => movie.id === id ? { ...movie, likes: movie.likes + 1 } : movie));
  }

  return (
    <>
      <Navbar />
      <main style={{ padding: '20px' }}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/movies" element={<Movies movies={movies} addLike={addLike} />} />
          <Route path="/about" element={<About movies={movies} />} />
        </Routes>
      </main>
    </>
  );
}

export default App;
