export default function MovieCard({ movie, deleteMovie, addLike }) {
  return (
    <div style={{
      border: '1px solid #ddd',
      margin: '0.5rem',
      padding: '1rem',
      borderRadius: '10px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
      maxWidth: '250px',
      textAlign: 'center'
    }}>
      <img src={movie.image} alt={movie.title} style={{ maxWidth: '100%', borderRadius: '8px' }} />
      <h3>{movie.title}</h3>
      <p>Year: {movie.year || 'N/A'}</p>
      <button onClick={() => deleteMovie(movie.id)}>Delete</button>
      {addLike && (
        <button onClick={() => addLike(movie.id)} style={{ marginLeft: '10px' }}>
          Like ({movie.likes || 0})
        </button>
      )}
    </div>
  );
}
