import { useState } from "react";

function AddMovieForm({ addMovie }) {
  const [title, setTitle] = useState("");
  const [director, setDirector] = useState("");

  function handleSubmit(e) {
    e.preventDefault();
    if (!title.trim() || !director.trim()) return;

    const configObj = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ title, director })
    };

    fetch("http://localhost:3001/movies", configObj)
      .then(res => {
        if (!res.ok) throw new Error("Erreur lors de l’ajout");
        return res.json();
      })
      .then(data => {
        addMovie(data);       // Update state app
        setTitle("");         // Reset our title field
        setDirector("");      // Reset realisator field
      })
      .catch(error => {
        alert("Impossible to add: " + error.message);
      });
  }

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
      <h2>Add a new movie</h2>
      <input
        type="text"
        placeholder="Titre du film"
        value={title}
        onChange={e => setTitle(e.target.value)}
      />
      <input
        type="text"
        placeholder="Réalisateur"
        value={director}
        onChange={e => setDirector(e.target.value)}
        style={{ marginLeft: "10px" }}
      />
      <button type="submit" style={{ marginLeft: "10px" }}>Add</button>
    </form>
  );
}

export default AddMovieForm;
