import React, { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Movies from './pages/Movies';
import About from './pages/About';
import AddMovieForm from './components/AddMovieForm';

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

  // Fetch  the movies at startup
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const response = await fetch(`${API_URL}/movies`);
        if (!response.ok) {
          throw new Error('Error in downloading movie.');
        }
        const data = await response.json();
        setMovies(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchMovies();
  }, [API_URL]);

  //  Add a movie
  const addMovie = (newMovie) => {
    setMovies(prev => [...prev, newMovie]);
  };

  //  Delete a movie
  const deleteMovie = async (id) => {
    if (!window.confirm('Do you want to delete this movie?')) return;

    try {
      await fetch(`${API_URL}/movies/${id}`, { method: 'DELETE' });
      setMovies(prev => prev.filter(movie => movie.id !== id));
    } catch {
      alert('Error to delete');
    }
  };

  return (
    <>
      <Navbar />
      <main style={{ padding: '20px' }}>
        {error && <p style={{ color: 'red' }}>{error}</p>}

        {isLoading ? (
          <p>Download movie..</p>
        ) : (
          <Routes>
            <Route path="/" element={<Home />} />
            <Route
              path="/movies"
              element={
                <>
                  <AddMovieForm addMovie={addMovie} />
                  {movies.length === 0 ? (
                    <p>No available movie for now.</p>
                  ) : (
                    <ul style={{ listStyle: 'none', padding: 0 }}>
                      {movies.map(movie => (
                        <li key={movie.id} style={{ marginBottom: '10px' }}>
                          <strong>{movie.title}</strong> â€” {movie.director}
                          <button
                            onClick={() => deleteMovie(movie.id)}
                            style={{ marginLeft: '10px' }}
                          >
                            delete
                          </button>
                        </li>
                      ))}
                    </ul>
                  )}
                </>
              }
            />
            <Route path="/about" element={<About />} />
          </Routes>
        )}
      </main>
    </>
  );
}

export default App;
