export default function MovieCard({ movie, addLike }) {
  return (
    <div
      style={{
        border: '1px solid #ddd',
        margin: '0.5rem',
        padding: '1rem',
        borderRadius: '10px',
        boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
        maxWidth: '250px',
        textAlign: 'center',
      }}
    >
      <img
        src={movie.image || 'https://via.placeholder.com/300x450?text=No+Image'}
        alt={movie.title}
        style={{ maxWidth: '100%', borderRadius: '8px' }}
        loading="lazy"
      />
      <h3>{movie.title}</h3>
      <p>Year: {movie.year || 'N/A'}</p>
      <button
        onClick={() => addLike(movie.id)}
        style={{
          background: '#ff4d4f',
          color: 'white',
          border: 'none',
          padding: '0.4rem 0.8rem',
          marginTop: '0.5rem',
          borderRadius: '6px',
          cursor: 'pointer',
          fontWeight: 'bold',
        }}
      >
        ❤️ Like ({movie.likes ?? 0})
      </button>
    </div>
  );
}
