import React, { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Movies from './pages/Movies';
import AddMovieForm from './components/AddMovieForm';
import Home from './pages/Home';
import About from './pages/About';

const BASE_URL = process.env.REACT_APP_BASE_URL || 'http://localhost:4000';

function App() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch(`${BASE_URL}/movies`)
      .then(res => res.json())
      .then(setMovies)
      .catch(err => console.error("Erreur fetch movies:", err));
  }, []);

  function addMovie(newMovie) {
    fetch(`${BASE_URL}/movies`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newMovie),
    })
      .then(res => res.json())
      .then(savedMovie => setMovies([...movies, savedMovie]))
      .catch(err => console.error("Erreur addMovie:", err));
  }

  function deleteMovie(id) {
    fetch(`${BASE_URL}/movies/${id}`, { method: 'DELETE' })
      .then(() => setMovies(movies.filter(m => m.id !== id)))
      .catch(err => console.error("Erreur deleteMovie:", err));
  }

  function addLike(id) {
    const target = movies.find(m => m.id === id);
    if (!target) return;

    fetch(`${BASE_URL}/movies/${id}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ likes: target.likes + 1 }),
    })
      .then(res => res.json())
      .then(updated => {
        setMovies(movies.map(m => (m.id === id ? updated : m)));
      })
      .catch(err => console.error("Erreur addLike:", err));
  }

  return (
    <>
      <Navbar />
      <main style={{ padding: '20px' }}>
        <AddMovieForm addMovie={addMovie} />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route
            path="/movies"
            element={
              <Movies
                movies={movies}
                addLike={addLike}
                deleteMovie={deleteMovie}
                addMovie={addMovie}
              />
            }
          />
          <Route path="/about" element={<About movies={movies} />} />
        </Routes>
      </main>
    </>
  );
}

export default App;
