import React, { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Movies from './pages/Movies';
import Home from './pages/Home';
import About from './pages/About';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:4000';

function App() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch(`${API_URL}/movies`)
      .then(res => res.json())
      .then(data => setMovies(data))
      .catch(err => console.error(err));
  }, []);

  function addLike(id) {
    const updatedMovie = movies.find(movie => movie.id === id);
    if (!updatedMovie) return;

    const newLikes = updatedMovie.likes + 1;

    fetch(`${API_URL}/movies/${id}`, {
      method: 'PATCH',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ likes: newLikes }),
    })
      .then(res => res.json())
      .then(updated => {
        setMovies(movies.map(movie => movie.id === id ? updated : movie));
      })
      .catch(err => console.error(err));
  }

  return (
    <>
      <Navbar />
      <main style={{ padding: '20px' }}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/movies" element={<Movies movies={movies} addLike={addLike} />} />
          <Route path="/about" element={<About movies={movies} />} />
        </Routes>
      </main>
    </>
  );
}

export default App;
